/**
 * Initializes a silent click tracking on a specific element by ID
 * @param {string} ELEMENT_ID - The ID of the HTML element to track
 * @param {string} CONTENT_CODE - Unique content code, used as path parameter
 * @param {string} API_URL - Base API URL for registering the click
 */
function initClick(ELEMENT_ID, CONTENT_CODE, API_URL) {
  const element = document.getElementById(ELEMENT_ID);
  if (!element) {
    console.warn(`Element with ID "${ELEMENT_ID}" not found.`);
    return;
  }

  element.addEventListener('click', () => {
    fetch(`${API_URL}/content-clicks/contentCode/${encodeURIComponent(CONTENT_CODE)}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: null // No payload required for this PATCH request
    }).catch(() => {
      // Fail silently, no user feedback
    });
  }, { once: true });
}
